</span></span><span style="display:flex;"><span>    <span style="color:#66d9ef">double</span> findMaxAverage(vector<span style="color:#f92672">&lt;</span><span style="color:#66d9ef">int</span><span style="color:#f92672">&gt;&amp;</span> nums, <span style="color:#66d9ef">int</span> k) {
</span></span><span style="display:flex;"><span>        <span style="color:#66d9ef">auto</span> be <span style="color:#f92672">=</span> nums.begin(), en <span style="color:#f92672">=</span> nums.end();
</span></span><span style="display:flex;"><span>        <span style="color:#66d9ef">double</span> sum <span style="color:#f92672">=</span> <span style="color:#ae81ff">0</span>;
</span></span><span style="display:flex;"><span>        <span style="color:#66d9ef">int</span> tmp0 <span style="color:#f92672">=</span> <span style="color:#ae81ff">0</span>, tmp1 <span style="color:#f92672">=</span> <span style="color:#ae81ff">0</span>;
</span></span><span style="display:flex;"><span>        <span style="color:#66d9ef">for</span>(<span style="color:#66d9ef">int</span> i <span style="color:#f92672">=</span> k; i <span style="color:#f92672">!=</span> <span style="color:#ae81ff">0</span>; <span style="color:#f92672">--</span>i, <span style="color:#f92672">++</span>be){
</span></span><span style="display:flex;"><span>            tmp1 <span style="color:#f92672">+=</span> <span style="color:#f92672">*</span>be;
</span></span><span style="display:flex;"><span>        }
</span></span><span style="display:flex;"><span>        sum <span style="color:#f92672">=</span> tmp1;
</span></span><span style="display:flex;"><span>        <span style="color:#66d9ef">for</span>(; be <span style="color:#f92672">!=</span> en; <span style="color:#f92672">++</span>be){
</span></span><span style="display:flex;"><span>            tmp0 <span style="color:#f92672">=</span> tmp1 <span style="color:#f92672">-</span> <span style="color:#f92672">*</span>(be <span style="color:#f92672">-</span> k) <span style="color:#f92672">+</span> <span style="color:#f92672">*</span>be;
</span></span><span style="display:flex;"><span>            <span style="color:#66d9ef">if</span>(tmp0 <span style="color:#f92672">&gt;</span> sum){
</span></span><span style="display:flex;"><span>                sum <span style="color:#f92672">=</span> tmp0;
</span></span><span style="display:flex;"><span>            }
</span></span><span style="display:flex;"><span>            tmp1 <span style="color:#f92672">=</span> tmp0;
</span></span><span style="display:flex;"><span>        }
</span></span><span style="display:flex;"><span>        <span style="color:#66d9ef">return</span> sum<span style="color:#f92672">/</span>k;
